class ResponsiveHeader{constructor(e){this.navigation=document.querySelector("."+e.navigation),this.burgerIcon=document.querySelector("#"+e.burgerIcon.open),this.closeBurgerIcon=document.querySelector("#"+e.burgerIcon.close),this.header=document.querySelector("."+e.header),this.htmlBody=document.querySelector("body"),this.BurgerClick(),this.closeBurgerClick(),this.WindowClick()}openState(){this.navigation.classList.add("active"),this.burgerIcon.classList.add("active"),this.htmlBody.classList.add("body-overlay"),this.header.classList.add("menuOpened")}closeState(){this.navigation.classList.remove("active"),this.burgerIcon.classList.remove("active"),this.htmlBody.classList.remove("body-overlay"),this.header.classList.remove("menuOpened")}BurgerClick(){this.burgerIcon.addEventListener("click",()=>{this.openState()})}closeBurgerClick(){this.closeBurgerIcon.addEventListener("click",()=>{this.closeState()})}WindowClick(){document.addEventListener("click",()=>{event.target.classList.contains("body-overlay")&&this.navigation.classList.contains("active")&&this.closeState()})}}const headerBurgerMenu=new ResponsiveHeader({navigation:"responsiveMenu",burgerIcon:{open:"open-menu-js",close:"close-menu-js"},header:"header"});