class responsiveHeader{constructor(e){this.navigation=document.querySelector("."+e.navigation),this.burgerIcon=document.querySelector("."+e.burgerIcon),this.burgerIcon_active=e.burgerIcon_active,this.htmlBody=document.querySelector("body"),this.BurgerClick(),this.WindowClick()}openState(){this.navigation.classList.add("responsiveMenu_active"),this.burgerIcon.classList.add(this.burgerIcon_active),this.htmlBody.classList.add("bodyOverlay")}closeState(){this.navigation.classList.remove("responsiveMenu_active"),this.burgerIcon.classList.remove(this.burgerIcon_active),this.htmlBody.classList.remove("bodyOverlay")}BurgerClick(){this.burgerIcon.addEventListener("click",()=>{event.currentTarget.classList.contains(this.burgerIcon_active)?this.closeState():this.openState()})}WindowClick(){document.addEventListener("click",()=>{event.target.classList.contains("bodyOverlay")&&this.closeState()})}}const headerBurgerMenu=new responsiveHeader({navigation:"responsiveMenu",burgerIcon:"burgerIcon",burgerIcon_active:"burgerIcon_active"});class TextSpoiler{constructor(e){if("string"==typeof e&&(e=document.querySelectorAll(e)),"function"==typeof e[Symbol.iterator])return[...e].map(e=>new TextSpoiler(e));this.$spoiler=e,this.$dropList=this.$spoiler.querySelector(".textSpoiler-dropList"),this.$button=this.$spoiler.querySelector(".textSpoiler-button"),this.handleClick(),this.hanldeClickWindow()}openState(){this.$spoiler.classList.add("opened")}closeState(){this.$spoiler.classList.remove("opened")}toggleState(){this.$spoiler.classList.contains("opened")?this.closeState():this.openState()}handleClick(){this.$button.addEventListener("click",()=>{this.toggleState()})}hanldeClickWindow(){window.addEventListener("click",()=>{event.target!=this.$button&&this.closeState()})}}const textSpoiler=new TextSpoiler(".textSpoiler");