var swiper=new Swiper(".swiper-container",{slidesPerView:"auto",speed:1e3,loop:!0,autoplay:{delay:7e3}});class responsiveHeader{constructor(e){this.navigation=document.querySelector("."+e.navigation),this.burgerIcon=document.querySelector("."+e.burgerIcon),this.burgerIcon_active=e.burgerIcon_active,this.htmlBody=document.querySelector("body"),this.BurgerClick(),this.WindowClick()}openState(){this.navigation.classList.add("active"),this.burgerIcon.classList.add(this.burgerIcon_active),this.htmlBody.classList.add("bodyOverlay")}closeState(){this.navigation.classList.remove("active"),this.burgerIcon.classList.remove(this.burgerIcon_active),this.htmlBody.classList.remove("bodyOverlay")}BurgerClick(){this.burgerIcon.addEventListener("click",()=>{event.currentTarget.classList.contains(this.burgerIcon_active)?this.closeState():this.openState()})}WindowClick(){document.addEventListener("click",()=>{event.target.classList.contains("bodyOverlay")&&this.closeState()})}}const headerBurgerMenu=new responsiveHeader({navigation:"navigation",burgerIcon:"burgerIcon",burgerIcon_active:"burgerIcon_active"});