class responsiveHeader{constructor(t){this.navigation=document.querySelector("."+t.navigation),this.burgerIcon=document.querySelector("."+t.burgerIcon),this.burgerIcon_active=t.burgerIcon_active,this.htmlBody=document.querySelector("body"),this.BurgerClick(),this.WindowClick()}openState(){this.navigation.classList.add("navigation_active"),this.burgerIcon.classList.add(this.burgerIcon_active),this.htmlBody.classList.add("body-lock"),this.htmlBody.classList.add("body-shadow")}closeState(){this.navigation.classList.remove("navigation_active"),this.burgerIcon.classList.remove(this.burgerIcon_active),this.htmlBody.classList.remove("body-lock"),this.htmlBody.classList.remove("body-shadow")}BurgerClick(){this.burgerIcon.addEventListener("click",()=>{event.currentTarget.classList.contains(this.burgerIcon_active)?this.closeState():this.openState()})}WindowClick(){document.addEventListener("click",()=>{event.target.classList.contains("body-lock","body-shadow")&&(console.log("asd"),this.closeState())})}}const headerBurgerMenu=new responsiveHeader({navigation:"navigation",burgerIcon:"header__burgerMenu",burgerIcon_active:"header__burgerMenu_active"}),dropListNavigation=()=>{const t=document.querySelectorAll(".dropList__toggleButton"),e=document.querySelectorAll(".dropList");t.forEach((t,o)=>{t.addEventListener("click",()=>e[o].classList.toggle("dropList_active"))})};dropListNavigation();const anchors=document.querySelectorAll('a[href*="#"]');for(let t of anchors)t.addEventListener("click",(function(e){e.preventDefault();const o=t.getAttribute("href").substr(1);document.getElementById(o).scrollIntoView({behavior:"smooth",block:"start"})}));window.addEventListener("load",t=>{const e=document.querySelector("#googleMap"),o=e.getAttribute("data-src");e.removeAttribute("data-src"),e.setAttribute("src",o)});