const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const i=e.getAttribute("href").substr(1);document.getElementById(i).scrollIntoView({behavior:"smooth",block:"start"})}));class responsiveHeader{constructor(e){this.navigation=document.querySelector("."+e.navigation),this.burgerIcon=document.querySelector("."+e.burgerIcon),this.htmlBody=document.querySelector("body"),this.BurgerClick(),this.WindowClick()}openState(){this.navigation.classList.add("active"),this.burgerIcon.classList.add("active"),this.htmlBody.classList.add("body-overlay")}closeState(){this.navigation.classList.remove("active"),this.burgerIcon.classList.remove("active"),this.htmlBody.classList.remove("body-overlay")}BurgerClick(){this.burgerIcon.addEventListener("click",()=>{event.currentTarget.classList.contains("active")?this.closeState():this.openState()})}WindowClick(){document.addEventListener("click",()=>{event.target.classList.contains("body-overlay")&&this.closeState()})}}const headerBurgerMenu=new responsiveHeader({navigation:"header",burgerIcon:"burgerIcon"});var navPanel=new Swiper(".navigationSlider__container",{spaceBetween:30,watchOverflow:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},breakpoints:{375:{slidesPerView:1},575:{slidesPerView:2},769:{slidesPerView:3}}}),sidebar=new Swiper(".sideBar__sliderContainer",{slidesPerView:1.4,spaceBetween:30});const resizeSlider=()=>{window.innerWidth>=768?(sidebar.destroy(!1,!0),console.log("slider destroy")):(sidebar.init(),console.log("slider init"))};window.addEventListener("resize",()=>{resizeSlider()}),resizeSlider();